export const rules = {
    required: (value) => !!value || "Este campo é obrigatório",
    email: (value) =>
      !value || /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value) || "Email inválido",
    keywords: (value) =>
      (value && value.split(",").length > 0) ||
      "Informe ao menos uma palavra-chave",
  };
