export const resetObraForm = (obraFormRef, obra) => {
    obraFormRef.reset();
    Object.keys(obra).forEach((key) => (obra[key] = null));
    obra.orientador = { matricula: "", nome: "", titulacao: "", areaAtuacao: "", email: "" };
    obra.coorientador = { matricula: "", nome: "", titulacao: "", areaAtuacao: "", email: "" };
    obra.possuiCoorientador = false;
  };
  
  export const handleFormSubmit = async (formRef, obra, cadastrarObra, router) => {
    if (formRef.validate()) {
      try {
        await cadastrarObra(obra);
        router.push("/HomePageAdm");
      } catch (error) {
        console.error(error);
      }
    }
  };
