export const rules = {
  required: (value) => !!value || "Este campo é obrigatório",
  email: (value) => !value || /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value) || "Email inválido",
  keywords: (value) =>
    (value && value.split(",").length > 0) || "Informe ao menos uma palavra-chave",
  
  // Regras para v-file-input
  requiredFile: (value) => !!value || "Arquivo é obrigatório",
  validFileType: (value) => !value || (value && value.type === "application/pdf") || "Apenas arquivos PDF são permitidos",
  maxFileSize: (value) => !value || (value && value.size <= 10 * 1024 * 1024) || "O tamanho do arquivo deve ser menor que 10MB",
};
