export default {
    data() {
      return {
        obra: {
          titulo: '',
          tipo: '',
          data_apresentacao: '',
          resumo: '',
          palavras_chave: ''
        },
        autor: {
          matricula_autor: '',
          nome_autor: '',
          email_autor: ''
        },
        orientador: {
          matricula_orientador: '',
          nome_orientador: '',
          titulo_academico: '',
          area_atuacao: '',
          email_orientador: ''
        },
        coorientador: {
          matricula_coorientador: '',
          nome_coorientador: '',
          titulo_academico: '',
          area_atuacao: '',
          email_coorientador: ''
        },
        curso: {
          nome_curso: ''
        }
      };
    },
    methods: {
      async submitForm() {
        try {
          const response = await axios.post('http://localhost:3000/cursos', {
            nome_curso: this.curso.nome_curso
          });
          console.log('Curso criado com sucesso:', response.data);
        } catch (error) {
          console.error('Erro ao criar o curso:', error);
        }
      }
    }
  };
