// src/router.js

import { createRouter, createWebHistory } from 'vue-router';
import HomePage from '@/views/HomePage.vue'; // Assumindo que HomePage.vue está em 'src/views/'

const routes = [
  {
    path: '/',
    name: 'HomePage',
    component: HomePage
  }
];

const router = createRouter({
  history: createWebHistory(import.meta.env.BASE_URL),  // Uso de 'import.meta.env.BASE_URL' para Vue 3 com Vite
  routes
});

export default router;
