export const rules = {
  required: [(v) => !!v || "Campo obrigatório"],
  email: [
    (v) => !!v || "Campo obrigatório",
    (v) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(v) || "Insira um email válido",
  ],
    keywords: (value) =>
      (value && value.split(",").length > 0) ||
      "Informe ao menos uma palavra-chave",
  };
