export default {
    data() {
      return {
        obra: {
          titulo: '',
          tipo: '',
          data_apresentacao: '',
          resumo: '',
          palavras_chave: '',
          arquivo: null,
        },
        autor: {
          matricula_autor: '',
          nome_autor: '',
          email_autor: '',
        },
        orientador: {
          matricula_orientador: '',
          nome_orientador: '',
          titulo_academico: '',
          area_atuacao: '',
          email_orientador: '',
        },
        coorientador: {
          matricula_coorientador: '',
          nome_coorientador: '',
          titulo_academico: '',
          area_atuacao: '',
          email_coorientador: '',
        },
        curso: {
          nome_curso: '',
        },
      };
    },
    methods: {
      onFileChange(e) {
        this.obra.arquivo = e.target.files[0];
      },
      submitForm() {
        // L칩gica de envio para o backend
        console.log("Formul치rio enviado!", this.obra, this.autor, this.orientador, this.coorientador, this.curso);
        // Pode usar axios ou fetch para enviar os dados ao servidor
      },
    },
  };
