// src/router.js
import { createRouter, createWebHistory } from 'vue-router';
import HomePage from '@/views/HomePage.vue';  // Verifique o caminho do componente

const routes = [
  {
    path: '/',
    name: 'HomePage',
    component: HomePage  // Certifique-se de que a HomePage.vue existe
  }
];

const router = createRouter({
  history: createWebHistory(import.meta.env.BASE_URL), // Use o correto para seu bundler (Vite ou Webpack)
  routes
});

export default router;
